import { FETCH_POST, NEW_POST } from '../actions/types';
import initialState from '../initialState';

const wordReducer = (initialState, action) => {
    switch(action.type) {

        case FETCH_POST: 
          return {
              ...state,
              items: action.payload
          }
        case NEW_POST:
          return {
              ...state,
              item: action.payload
          }
        default:
          return state;           
    }
}

export default wordReducer;
